              {state.lastError}
            </div>
          ) : null}

          <MessageList
            messages={state.messages}
            isLoading={isLoading}
            activeAgentLabel={activeAgentLabel}
            onSuggestionClick={handleSuggestionClick}
          />

          <MessageInput
            ref={messageInputRef}
            onSend={handleSend}
            isLoading={isLoading}
            disabled={!state.connected}
            allowWhileLoading={activeAgentId !== 'manager'}
            agentLabel={activeAgentLabel}
          />
        </div>
      </div>
    </main>
  )
}
"}]}

