import { useEffect, useRef } from 'react'
import { Loader2 } from 'lucide-react'
import { cn } from '@/lib/utils'
import type { ServerEvent } from '@/lib/ws-types'

type ConversationMessage = Extract<ServerEvent, { type: 'conversation_message' }>

interface MessageListProps {
  messages: ConversationMessage[]
  isLoading: boolean
  activeAgentLabel: string
  onSuggestionClick?: (suggestion: string) => void
}

const suggestions = ['Plan a swarm workflow', 'Debug manager state', 'Summarize latest run']

function formatTimestamp(iso: string): string {
  try {
    return new Date(iso).toLocaleTimeString([], { hour: 'numeric', minute: '2-digit' })
  } catch {
    return ''
  }
}

function EmptyState({ onSuggestionClick }: { onSuggestionClick?: (suggestion: string) => void }) {
  return (
    <div className="flex h-full flex-col items-center justify-center p-6 text-center">
      <h2 className="mb-4 text-base font-medium text-foreground">What can I do for you?</h2>
      {onSuggestionClick ? (
        <div className="flex max-w-[320px] flex-wrap justify-center gap-2">
          {suggestions.map((suggestion) => (
            <button
              key={suggestion}
              onClick={() => onSuggestionClick(suggestion)}
              type="button"
              className="rounded-full border border-border bg-muted px-3 py-1.5 text-sm text-foreground transition-colors hover:bg-muted/80"
            >
              {suggestion}
            </button>
          ))}
        </div>
      ) : null}
      <p className="mt-6 text-xs text-muted-foreground">AI can make mistakes. Always verify important actions.</p>
    </div>
  )
}

function LoadingIndicator() {
  return (
    <div className="flex justify-start" role="status" aria-live="polite" aria-label="Agent is working">
      <div className="flex items-center gap-0.5">
        <Loader2 className="size-3.5 animate-spin text-foreground/50" />
      </div>
    </div>
  )
}

function Message({ message, activeAgentLabel }: { message: ConversationMessage; activeAgentLabel: string }) {
  const isUser = message.role === 'user'
  const isSystem = message.role === 'system'

  if (isSystem) {
    return (
      <div className="rounded-lg border border-amber-200 bg-amber-50 px-3 py-2 text-amber-900">
        <div className="mb-1 text-[11px] uppercase tracking-wide text-amber-700">System • {formatTimestamp(message.timestamp)}</div>
        <p className="whitespace-pre-wrap break-words text-sm leading-relaxed">{message.text}</p>
      </div>
    )
  }

  if (isUser) {
    const fromUser = message.source === 'user_input'

    return (
      <div className="flex justify-end">
        <div className="max-w-[85%] rounded-lg rounded-tr-sm bg-primary px-3 py-2 text-primary-foreground">
          <div className="mb-1 text-[11px] uppercase tracking-wide text-primary-foreground/80">
            {fromUser ? 'You' : 'Input'} • {formatTimestamp(message.timestamp)}
          </div>
          <p className="whitespace-pre-wrap break-words text-sm leading-relaxed">{message.text}</p>
        </div>
      </div>
    )
  }

  return (
    <div className="text-foreground">
      <div className="mb-1 text-[11px] uppercase tracking-wide text-muted-foreground">
        {activeAgentLabel} • {formatTimestamp(message.timestamp)} • {message.source}
      </div>
      <p className="whitespace-pre-wrap break-words text-sm leading-relaxed">{message.text}</p>
    </div>
  )
}

export function MessageList({ messages, isLoading, activeAgentLabel, onSuggestionClick }: MessageListProps) {
  const bottomRef = useRef<HTMLDivElement | null>(null)

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth', block: 'end' })
  }, [messages, isLoading])

  if (messages.length === 0 && !isLoading) {
    return <EmptyState onSuggestionClick={onSuggestionClick} />
  }

  return (
    <div
      className={cn(
        'flex-1 min-h-0 overflow-y-auto',
        '[&::-webkit-scrollbar]:w-1.5 [&::-webkit-scrollbar-track]:bg-transparent',
        '[&::-webkit-scrollbar-thumb]:rounded-full [&::-webkit-scrollbar-thumb]:bg-transparent',
        '[scrollbar-width:thin] [scrollbar-color:transparent_transparent]',
        'hover:[&::-webkit-scrollbar-thumb]:bg-border hover:[scrollbar-color:var(--color-border)_transparent]',
      )}
    >
      <div className={cn('space-y-3 p-3')}>
        {messages.map((message, index) => (
          <Message key={`${message.timestamp}-${index}-${message.role}`} message={message} activeAgentLabel={activeAgentLabel} />
        ))}
        {isLoading ? <LoadingIndicator /> : null}
        <div ref={bottomRef} />
      </div>
    </div>
  )
}

import {
  forwardRef,
  useCallback,
  useEffect,
  useImperativeHandle,
  useRef,
  useState,
  type FormEvent,
  type KeyboardEvent,
} from 'react'
import { ArrowUp, Loader2 } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { cn } from '@/lib/utils'

interface MessageInputProps {
  onSend: (message: string) => void
  isLoading: boolean
  disabled?: boolean
  agentLabel?: string
}

export interface MessageInputHandle {
  setInput: (value: string) => void
  focus: () => void
}

export const MessageInput = forwardRef<MessageInputHandle, MessageInputProps>(function MessageInput(
  { onSend, isLoading, disabled = false, agentLabel = 'agent' },
  ref,
) {
  const [input, setInput] = useState('')
  const textareaRef = useRef<HTMLTextAreaElement | null>(null)

  const resizeTextarea = useCallback(() => {
    const textarea = textareaRef.current
    if (!textarea) return

    textarea.style.height = 'auto'
    textarea.style.height = `${Math.min(textarea.scrollHeight, 220)}px`
  }, [])

  useEffect(() => {
    resizeTextarea()
  }, [input, resizeTextarea])

  useEffect(() => {
    if (!disabled && !isLoading) {
      textareaRef.current?.focus()
    }
  }, [disabled, isLoading])

  useImperativeHandle(
    ref,
    () => ({
      setInput: (value: string) => {
        setInput(value)
        requestAnimationFrame(() => textareaRef.current?.focus())
      },
      focus: () => {
        textareaRef.current?.focus()
      },
    }),
    [],
  )

  const handleSubmit = useCallback(
    (event: FormEvent<HTMLFormElement>) => {
      event.preventDefault()
      const trimmed = input.trim()
      if (!trimmed || disabled || isLoading) return

      onSend(trimmed)
      setInput('')
    },
    [disabled, input, isLoading, onSend],
  )

  const handleKeyDown = (event: KeyboardEvent<HTMLTextAreaElement>) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault()
      const trimmed = input.trim()
      if (!trimmed || disabled || isLoading) return
      onSend(trimmed)
      setInput('')
    }
  }

  const canSubmit = input.trim().length > 0 && !disabled && !isLoading

  return (
    <form onSubmit={handleSubmit} className="sticky bottom-0 bg-background p-3">
      <div className="overflow-hidden rounded-2xl border border-border">
        <div className="group relative flex">
          <textarea
            ref={textareaRef}
            value={input}
            onChange={(event) => setInput(event.target.value)}
            onKeyDown={handleKeyDown}
            placeholder={disabled ? 'Waiting for connection...' : `Message ${agentLabel}...`}
            disabled={disabled}
            rows={1}
            className={cn(
              'flex-1 resize-none border-0 bg-transparent text-sm text-foreground shadow-none focus:outline-none',
              'min-h-[68px] max-h-[220px]',
              'px-4 pt-3 pb-11',
              '[&::-webkit-scrollbar]:w-1.5',
              '[&::-webkit-scrollbar-track]:bg-transparent',
              '[&::-webkit-scrollbar-thumb]:bg-transparent',
              '[&::-webkit-scrollbar-thumb]:rounded-full',
              'group-hover:[&::-webkit-scrollbar-thumb]:bg-border',
            )}
          />

          <div className="absolute bottom-1.5 right-1.5 z-10 flex items-center gap-1">
            <Button
              type="submit"
              disabled={!canSubmit}
              size="icon"
              className={cn(
                'size-7 rounded-full transition-all',
                canSubmit
                  ? 'bg-primary text-primary-foreground hover:bg-primary/90 active:scale-95'
                  : 'cursor-default bg-muted text-muted-foreground/40',
              )}
            >
              {isLoading ? <Loader2 className="size-3.5 animate-spin" /> : <ArrowUp className="size-3.5" strokeWidth={2.5} />}
            </Button>
          </div>
        </div>
      </div>
    </form>
  )
})

import { useEffect, useMemo, useRef, useState } from 'react'
import { createFileRoute } from '@tanstack/react-router'
import { AgentSidebar } from '@/components/chat/AgentSidebar'
import { ChatHeader } from '@/components/chat/ChatHeader'
import { MessageInput, type MessageInputHandle } from '@/components/chat/MessageInput'
import { MessageList } from '@/components/chat/MessageList'
import { ManagerWsClient, type ManagerWsState } from '@/lib/ws-client'

export const Route = createFileRoute('/')({
  component: IndexPage,
})

function IndexPage() {
  const wsUrl = import.meta.env.VITE_SWARM_WS_URL ?? 'ws://127.0.0.1:47187'
  const clientRef = useRef<ManagerWsClient | null>(null)
  const messageInputRef = useRef<MessageInputHandle | null>(null)

  const [state, setState] = useState<ManagerWsState>({
    connected: false,
    targetAgentId: 'manager',
    subscribedAgentId: null,
    messages: [],
    agents: [],
    statuses: {},
    lastError: null,
  })

  useEffect(() => {
    const client = new ManagerWsClient(wsUrl, 'manager')
    clientRef.current = client
    setState(client.getState())

    const unsubscribe = client.subscribe((next) => {
      setState(next)
    })

    client.start()

    return () => {
      unsubscribe()
      if (clientRef.current === client) {
        clientRef.current = null
      }
      client.destroy()
    }
  }, [wsUrl])

  const activeAgentId = state.targetAgentId ?? state.subscribedAgentId ?? 'manager'

  const activeAgent = useMemo(() => {
    return state.agents.find((agent) => agent.agentId === activeAgentId) ?? null
  }, [activeAgentId, state.agents])

  const activeAgentLabel = activeAgent?.displayName ?? (activeAgentId === 'manager' ? 'Manager' : activeAgentId)

  const isLoading = useMemo(() => {
    const fromStatuses = state.statuses[activeAgentId]?.status
    if (fromStatuses) return fromStatuses === 'streaming'

    const fromAgents = state.agents.find((agent) => agent.agentId === activeAgentId)?.status
    return fromAgents === 'streaming'
  }, [activeAgentId, state.agents, state.statuses])

  const handleSend = (text: string) => {
    clientRef.current?.sendUserMessage(text, {
      agentId: activeAgentId,
      delivery: activeAgentId === 'manager' ? 'steer' : 'auto',
    })
  }

  const handleNewChat = () => {
    clientRef.current?.sendUserMessage('/new', { agentId: 'manager', delivery: 'steer' })
  }

  const handleSelectAgent = (agentId: string) => {
    clientRef.current?.subscribeToAgent(agentId)
  }

  const handleSuggestionClick = (prompt: string) => {
    messageInputRef.current?.setInput(prompt)
  }

  return (
    <main className="h-screen bg-background text-foreground">
      <div className="mx-auto flex h-screen w-full max-w-[1400px] border-x border-border/60 bg-background shadow-sm">
        <AgentSidebar
          connected={state.connected}
          agents={state.agents}
          statuses={state.statuses}
          selectedAgentId={activeAgentId}
          onSelectAgent={handleSelectAgent}
        />

        <div className="flex min-w-0 flex-1 flex-col">
          <ChatHeader
            connected={state.connected}
            activeAgentId={activeAgentId}
            activeAgentLabel={`${activeAgentLabel} Chat`}
            showNewChat={activeAgentId === 'manager'}
            onNewChat={handleNewChat}
          />

          {state.lastError ? (
            <div className="border-b border-destructive/20 bg-destructive/10 px-3 py-2 text-xs text-destructive">
              {state.lastError}
            </div>
          ) : null}

          <MessageList
            messages={state.messages}
            isLoading={isLoading}
            activeAgentLabel={activeAgentLabel}
            onSuggestionClick={handleSuggestionClick}
          />

          <MessageInput
            ref={messageInputRef}
            onSend={handleSend}
            isLoading={isLoading}
            disabled={!state.connected}
            agentLabel={activeAgentLabel}
          />
        </div>
      </div>
    </main>
  )
}

