{"content":[{"type":"text","text":"import { useState } from "react";
import { ChevronRight, Check, Loader2, X, AlertCircle, Globe } from "lucide-react";
import { Collapsible, CollapsibleTrigger, CollapsibleContent } from "@/components/ui/collapsible";
import { cn } from "@/lib/utils";
import type { UIToolCallPart, UIToolCallState } from "@/lib/message-types";

interface ToolCallCardProps {
  toolPart: UIToolCallPart;
}

// Truncate helper for displaying details in collapsed view
function truncate(str: string | undefined | null, maxLen: number): string {
  if (!str) return "";
  return str.length > maxLen ? str.slice(0, maxLen) + "..." : str;
}

// Tool configuration with friendly labels and detail extractors
import type { LucideIcon } from "lucide-react";

const TOOL_CONFIG: Record<
  string,
  {
    label: string;
    activeLabel: string;
    cancelledLabel: string;
    errorLabel: string;
    icon?: LucideIcon;
    getDetail?: (input: Record<string, unknown>) => string | null;
    /** Override the full friendly message (skips "label: detail" format) */
    getMessage?: (input: Record<string, unknown>, baseLabel: string) => string | null;
    getExpandedContent?: (
      input: Record<string, unknown>
    ) => { label: string; content: string } | null;
  }
> = {
  bash: {
    label: "Ran command",
    activeLabel: "Running command",
    cancelledLabel: "Cancelled",
    errorLabel: "Command failed",
    getMessage: (input, baseLabel) => {
      const desc = input.description as string | undefined;
      if (desc) return desc;
      const cmd = truncate(input.command as string, 60);
      return cmd ? `${baseLabel}: ${cmd}` : null;
    },
    getExpandedContent: (input) =>
      input.command ? { label: "Command", content: input.command as string } : null,
  },
  readFile: {
    label: "Read file",
    activeLabel: "Reading file",
    cancelledLabel: "Cancelled",
    errorLabel: "Read failed",
    getDetail: (input) => truncate(input.path as string, 60),
    getExpandedContent: (input) =>
      input.path ? { label: "Path", content: input.path as string } : null,
  },
  writeFile: {
    label: "Wrote file",
    activeLabel: "Writing file",
    cancelledLabel: "Cancelled",
    errorLabel: "Write failed",
    getDetail: (input) => truncate(input.path as string, 60),
    getExpandedContent: (input) => {
      const parts: string[] = [];
      if (input.path) parts.push(`Path: ${input.path}`);
      if (input.content) parts.push(`Content:\n${input.content}`);
      return parts.length > 0 ? { label: "Details", content: parts.join("\n\n") } : null;
    },
  },
  loadSkill: {
    label: "Loaded skill",
    activeLabel: "Loading skill",
    cancelledLabel: "Cancelled",
    errorLabel: "Skill load failed",
    getDetail: (input) => input.name as string | null,
    getExpandedContent: (input) =>
      input.name ? { label: "Skill", content: input.name as string } : null,
  },
  webSearch: {
    label: "Searched the web",
    activeLabel: "Searching the web",
    cancelledLabel: "Search cancelled",
    errorLabel: "Search failed",
    icon: Globe,
    getDetail: (input) => truncate(input.query as string, 60),
    getExpandedContent: (input) =>
      input.query ? { label: "Query", content: input.query as string } : null,
  },
};

type ToolDisplayStatus = "pending" | "completed" | "cancelled" | "error";

function mapState(state: UIToolCallState): ToolDisplayStatus {
  switch (state) {
    case "completed":
      return "completed";
    case "cancelled":
      return "cancelled";
    case "error":
      return "error";
    default:
      return "pending";
  }
}

function getFriendlyMessage(
  toolName: string,
  input: Record<string, unknown>,
  status: ToolDisplayStatus
): string {
  const config = TOOL_CONFIG[toolName];
  if (!config) {
    switch (status) {
      case "completed":
        return "Completed task";
      case "cancelled":
        return "Cancelled";
      case "error":
        return "Task failed";
      default:
        return "Working...";
    }
  }

  let baseLabel: string;
  switch (status) {
    case "completed":
      baseLabel = config.label;
      break;
    case "cancelled":
      baseLabel = config.cancelledLabel;
      break;
    case "error":
      baseLabel = config.errorLabel;
      break;
    default:
      baseLabel = config.activeLabel;
  }

  if (config.getMessage) {
    return config.getMessage(input, baseLabel) ?? baseLabel;
  }

  const detail = config.getDetail?.(input);
  return detail ? `${baseLabel}: ${detail}` : baseLabel;
}

function getExpandedContent(toolName: string, input: Record<string, unknown>) {
  return TOOL_CONFIG[toolName]?.getExpandedContent?.(input) ?? null;
}

export function ToolCallCard({ toolPart }: ToolCallCardProps) {
  const [isExpanded, setIsExpanded] = useState(false);
  const { state, input, output, errorText, toolName } = toolPart;

  const inputRecord = (input ?? {}) as Record<string, unknown>;
  const status = mapState(state);

  // Check if this was an abort/cancellation
  const isCancelled =
    status === "error" &&
    typeof errorText === "string" &&
    errorText.includes("[Aborted]");

  const displayStatus: ToolDisplayStatus = isCancelled ? "cancelled" : status;

  const friendlyMessage = getFriendlyMessage(toolName, inputRecord, displayStatus);
  const expandedContent = getExpandedContent(toolName, inputRecord);
  const CustomIcon = TOOL_CONFIG[toolName]?.icon;

  // Format output for display
  const formatOutput = (out: unknown): string => {
    if (typeof out === "string") return out;
    if (typeof out === "object" && out !== null && "output" in out) {
      return String((out as { output: unknown }).output);
    }
    return JSON.stringify(out, null, 2);
  };

  return (
    <Collapsible open={isExpanded} onOpenChange={setIsExpanded}>
      <CollapsibleTrigger asChild>
        <button
          className={cn(
            "group flex items-start gap-1.5 text-left",
            "text-sm text-foreground/70 italic",
            "hover:text-foreground transition-colors"
          )}
        >
          {displayStatus === "completed" ? (
            CustomIcon ? (
              <CustomIcon className="size-3.5 mt-1 text-green-600 dark:text-green-400 flex-shrink-0" />
            ) : (
              <Check className="size-3.5 mt-1 text-green-600 dark:text-green-400 flex-shrink-0" />
            )
          ) : displayStatus === "cancelled" ? (
            <X className="size-3.5 mt-1 text-red-500 dark:text-red-400 flex-shrink-0" />
          ) : displayStatus === "error" ? (
            <AlertCircle className="size-3.5 mt-1 text-amber-500 dark:text-amber-400 flex-shrink-0" />
          ) : (
            <Loader2 className="size-3.5 mt-1 text-foreground/50 animate-spin flex-shrink-0" />
          )}
          <span>{friendlyMessage}</span>
          <ChevronRight
            className={cn(
              "size-3.5 mt-1 flex-shrink-0 transition-all",
              "opacity-0 group-hover:opacity-100",
              isExpanded && "rotate-90"
            )}
          />
        </button>
      </CollapsibleTrigger>

      <CollapsibleContent>
        <div className="mt-2 ml-4 space-y-2 text-sm">
          {expandedContent && (
            <div>
              <p className="text-xs font-medium text-muted-foreground uppercase tracking-wide mb-1">
                {expandedContent.label}
              </p>
              <pre className="p-2 rounded bg-muted/50 overflow-x-auto font-mono text-xs whitespace-pre-wrap">
                {expandedContent.content}
              </pre>
            </div>
          )}

          {(output !== undefined || errorText !== undefined) && (
            <div>
              <p className="text-xs font-medium text-muted-foreground uppercase tracking-wide mb-1">
                {displayStatus === "cancelled" ? "Cancelled" : displayStatus === "error" ? "Error" : "Result"}
              </p>
              <pre
                className={cn(
                  "p-2 rounded overflow-x-auto font-mono text-xs whitespace-pre-wrap",
                  displayStatus === "cancelled"
                    ? "bg-red-500/10"
                    : displayStatus === "error"
                      ? "bg-amber-500/10"
                      : "bg-muted/30"
                )}
              >
                {output !== undefined ? formatOutput(output) : errorText}
              </pre>
            </div>
          )}

          {toolPart.images?.map((img, i) => (
            <img
              key={i}
              src={img.url}
              alt={img.filename || "Screenshot"}
              className="max-w-xs rounded-lg border border-border"
            />
          ))}
        </div>
      </CollapsibleContent>
    </Collapsible>
  );
}
"}]}

