            <div>
              <p className="text-xs font-medium text-muted-foreground uppercase tracking-wide mb-1">
                {expandedContent.label}
              </p>
              <pre className="p-2 rounded bg-muted/50 overflow-x-auto font-mono text-xs whitespace-pre-wrap">
                {expandedContent.content}
              </pre>
            </div>
          )}

          {(output !== undefined || errorText !== undefined) && (
            <div>
              <p className="text-xs font-medium text-muted-foreground uppercase tracking-wide mb-1">
                {displayStatus === "cancelled" ? "Cancelled" : displayStatus === "error" ? "Error" : "Result"}
              </p>
              <pre
                className={cn(
                  "p-2 rounded overflow-x-auto font-mono text-xs whitespace-pre-wrap",
                  displayStatus === "cancelled"
                    ? "bg-red-500/10"
                    : displayStatus === "error"
                      ? "bg-amber-500/10"
                      : "bg-muted/30"
                )}
              >
                {output !== undefined ? formatOutput(output) : errorText}
              </pre>
            </div>
          )}

          {toolPart.images?.map((img, i) => (
            <img
              key={i}
              src={img.url}
              alt={img.filename || "Screenshot"}
              className="max-w-xs rounded-lg border border-border"
            />
          ))}
        </div>
      </CollapsibleContent>
    </Collapsible>
  );
}

