import { describe, it, expect, beforeEach, vi } from "vitest";
import { render, screen } from "@testing-library/react";
import { MessageList } from "./MessageList";
import type { UIMessage } from "@/lib/message-types";

describe("MessageList", () => {
  beforeEach(() => {
    Object.defineProperty(Element.prototype, "scrollIntoView", {
      value: vi.fn(),
      writable: true,
      configurable: true,
    });
  });

  it("does not render legacy writeFile footer cards", () => {
    const messages: UIMessage[] = [
      {
        id: "asst_1",
        role: "assistant",
        parts: [
          { type: "text", text: "Done writing the file." },
          {
            type: "tool-call",
            toolCallId: "tool_1",
            toolName: "writeFile",
            input: { path: "/workspace/report.csv", content: "a,b\n1,2" },
            state: "completed",
          },
        ],
      },
    ];

    render(<MessageList messages={messages} isLoading={false} />);

    expect(screen.queryByText("/workspace/report.csv")).toBeNull();
    expect(screen.queryByTitle("Download")).toBeNull();
    expect(screen.queryByTitle("View")).toBeNull();
  });

  it("shows a bottom loading indicator while chat is loading", () => {
    const messages: UIMessage[] = [
      {
        id: "asst_1",
        role: "assistant",
        parts: [{ type: "text", text: "Working..." }],
      },
    ];

    render(<MessageList messages={messages} isLoading />);

    expect(screen.getByRole("status", { name: "Assistant is working" })).not.toBeNull();
  });

  it("hides the loading indicator when chat is not loading", () => {
    const messages: UIMessage[] = [
      {
        id: "asst_1",
        role: "assistant",
        parts: [{ type: "text", text: "Done." }],
      },
    ];

    render(<MessageList messages={messages} isLoading={false} />);

    expect(screen.queryByRole("status", { name: "Assistant is working" })).toBeNull();
  });
});

