{
  "name": "swarm",
  "private": true,
  "packageManager": "pnpm@10.8.0",
  "type": "module",
  "workspaces": [
    "apps/backend",
    "apps/ui"
  ],
  "scripts": {
    "dev": "concurrently --kill-others-on-fail --names backend,ui \"pnpm --filter @swarm/backend dev\" \"pnpm --filter @swarm/ui dev\"",
    "dev:backend": "pnpm --filter @swarm/backend dev",
    "dev:ui": "pnpm --filter @swarm/ui dev",
    "build": "pnpm --filter @swarm/backend build && pnpm --filter @swarm/ui build",
    "test": "pnpm --filter @swarm/backend test && pnpm --filter @swarm/ui test"
  },
  "devDependencies": {
    "concurrently": "^9.2.1"
  }
}

{
  "name": "@swarm/backend",
  "private": true,
  "type": "module",
  "scripts": {
    "dev": "tsx watch --exclude \"../../data/**\" --exclude \"data/**\" src/index.ts",
    "build": "tsc -p tsconfig.build.json",
    "start": "node dist/index.js",
    "test": "vitest run"
  },
  "dependencies": {
    "@mariozechner/pi-ai": "^0.52.12",
    "@mariozechner/pi-coding-agent": "^0.52.12",
    "@sinclair/typebox": "^0.34.41",
    "ws": "^8.18.0"
  },
  "devDependencies": {
    "@types/node": "^24.3.0",
    "@types/ws": "^8.18.1",
    "tsx": "^4.20.3",
    "typescript": "^5.9.2",
    "vitest": "^3.2.4"
  }
}

