import { WebSocketServer, type RawData, WebSocket } from "ws";
import type { ClientCommand, ServerEvent } from "../protocol/ws-types.js";
import type { SwarmManager } from "../swarm/swarm-manager.js";

export class SwarmWebSocketServer {
  private readonly swarmManager: SwarmManager;
  private readonly host: string;
  private readonly port: number;
  private readonly allowNonManagerSubscriptions: boolean;

  private wss: WebSocketServer | null = null;
  private readonly subscriptions = new Map<WebSocket, string>();

  private readonly onConversationMessage = (event: ServerEvent): void => {
    if (event.type !== "conversation_message") return;
    this.broadcastToSubscribed(event);
  };

  private readonly onConversationReset = (event: ServerEvent): void => {
    if (event.type !== "conversation_reset") return;
    this.broadcastToSubscribed(event);
  };

  private readonly onAgentStatus = (event: ServerEvent): void => {
    if (event.type !== "agent_status") return;
    this.broadcastToSubscribed(event);
  };

  private readonly onAgentsSnapshot = (event: ServerEvent): void => {
    if (event.type !== "agents_snapshot") return;
    this.broadcastToSubscribed(event);
  };

  constructor(options: {
    swarmManager: SwarmManager;
    host: string;
    port: number;
    allowNonManagerSubscriptions: boolean;
  }) {
    this.swarmManager = options.swarmManager;
    this.host = options.host;
    this.port = options.port;
    this.allowNonManagerSubscriptions = options.allowNonManagerSubscriptions;
  }

  async start(): Promise<void> {
    if (this.wss) return;

    const wss = new WebSocketServer({
      host: this.host,
      port: this.port
    });
    this.wss = wss;

    this.wss.on("connection", (socket) => {
      socket.on("message", (raw) => {
        void this.handleSocketMessage(socket, raw);
      });

      socket.on("close", () => {
        this.subscriptions.delete(socket);
      });

      socket.on("error", () => {
        this.subscriptions.delete(socket);
      });
    });

    await new Promise<void>((resolve, reject) => {
      const onListening = (): void => {
        cleanup();
        resolve();
      };

      const onError = (error: Error): void => {
        cleanup();
        reject(error);
      };

      const cleanup = (): void => {
        wss.off("listening", onListening);
        wss.off("error", onError);
      };

      wss.on("listening", onListening);
      wss.on("error", onError);
    });

    this.swarmManager.on("conversation_message", this.onConversationMessage);
    this.swarmManager.on("conversation_reset", this.onConversationReset);
    this.swarmManager.on("agent_status", this.onAgentStatus);
    this.swarmManager.on("agents_snapshot", this.onAgentsSnapshot);
  }

  async stop(): Promise<void> {
    this.swarmManager.off("conversation_message", this.onConversationMessage);
    this.swarmManager.off("conversation_reset", this.onConversationReset);
    this.swarmManager.off("agent_status", this.onAgentStatus);
    this.swarmManager.off("agents_snapshot", this.onAgentsSnapshot);

    const current = this.wss;
    this.wss = null;
    this.subscriptions.clear();

    if (!current) return;

    await new Promise<void>((resolve, reject) => {
      current.close((error) => {
        if (error) {
          reject(error);
          return;
        }
        resolve();
      });
    });
  }

  private async handleSocketMessage(socket: WebSocket, raw: RawData): Promise<void> {
    const parsed = this.parseClientCommand(raw);
    if (!parsed.ok) {
      this.send(socket, {
        type: "error",
        code: "INVALID_COMMAND",
        message: parsed.error
      });
      return;
    }

    const command = parsed.command;

    if (command.type === "ping") {
      this.send(socket, {
        type: "ready",
        serverTime: new Date().toISOString(),
        subscribedAgentId: this.subscriptions.get(socket) ?? this.swarmManager.getConfig().managerId
      });
      return;
    }

    if (command.type === "subscribe") {
      const targetAgent = command.agentId ?? this.swarmManager.getConfig().managerId;

      if (!this.allowNonManagerSubscriptions && targetAgent !== this.swarmManager.getConfig().managerId) {
        this.send(socket, {
          type: "error",
          code: "SUBSCRIPTION_NOT_SUPPORTED",
          message: `Subscriptions are currently limited to ${this.swarmManager.getConfig().managerId}.`
        });
        return;
      }

      this.subscriptions.set(socket, targetAgent);

      this.send(socket, {
        type: "ready",
        serverTime: new Date().toISOString(),
        subscribedAgentId: targetAgent
      });
      this.send(socket, {
        type: "agents_snapshot",
        agents: this.swarmManager.listAgents()
      });
      this.send(socket, {
        type: "conversation_history",
        agentId: targetAgent,
        messages: this.swarmManager.getConversationHistory(targetAgent)
      });
      return;
    }

    if (command.type === "user_message") {
      if (!this.subscriptions.has(socket)) {
        this.send(socket, {
          type: "error",
          code: "NOT_SUBSCRIBED",
          message: "Send subscribe before user_message."
        });
        return;
      }

      try {
        if (command.text.trim() === "/new") {
          await this.swarmManager.resetManagerSession("user_new_command");
          return;
        }
        await this.swarmManager.handleUserMessage(command.text);
      } catch (error) {
        this.send(socket, {
          type: "error",
          code: "USER_MESSAGE_FAILED",
          message: error instanceof Error ? error.message : String(error)
        });
      }
      return;
    }
  }

  private broadcastToSubscribed(event: ServerEvent): void {
    if (!this.wss) return;

    for (const client of this.wss.clients) {
      if (client.readyState !== WebSocket.OPEN) continue;

      const subscribedAgent = this.subscriptions.get(client);
      if (!subscribedAgent) continue;

      if (event.type === "conversation_message" || event.type === "conversation_reset") {
        if (subscribedAgent !== this.swarmManager.getConfig().managerId) {
          continue;
        }
      }

      this.send(client, event);
    }
  }

  private send(socket: WebSocket, event: ServerEvent): void {
    if (socket.readyState !== WebSocket.OPEN) return;
    socket.send(JSON.stringify(event));
  }

  private parseClientCommand(raw: RawData):
    | { ok: true; command: ClientCommand }
    | { ok: false; error: string } {
    const text = typeof raw === "string" ? raw : raw.toString("utf8");

    let parsed: unknown;
    try {
      parsed = JSON.parse(text);
    } catch {
      return { ok: false, error: "Command must be valid JSON" };
    }

    if (!parsed || typeof parsed !== "object") {
      return { ok: false, error: "Command must be a JSON object" };
    }

    const maybe = parsed as Partial<ClientCommand> & { type?: unknown };

    if (maybe.type === "ping") {
      return { ok: true, command: { type: "ping" } };
    }

    if (maybe.type === "subscribe") {
      if (maybe.agentId !== undefined && typeof maybe.agentId !== "string") {
        return { ok: false, error: "subscribe.agentId must be a string when provided" };
      }
      return { ok: true, command: { type: "subscribe", agentId: maybe.agentId } };
    }

    if (maybe.type === "user_message") {
      if (typeof maybe.text !== "string" || maybe.text.trim().length === 0) {
        return { ok: false, error: "user_message.text must be a non-empty string" };
      }
      return { ok: true, command: { type: "user_message", text: maybe.text } };
    }

    return { ok: false, error: "Unknown command type" };
  }
}

import { Component, Suspense, useCallback, useEffect, useMemo, type ReactNode } from "react";
import { useLiveQuery } from "dexie-react-hooks";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { useAppStore } from "@/stores/app-store";
import { Header } from "@/components/layout/Header";
import { AnnouncementBanner } from "@/components/layout/AnnouncementBanner";
import { ThreadListView } from "@/components/threads/ThreadListView";
import { ChatView } from "@/components/chat/ChatView";
import { SettingsView } from "@/components/settings/SettingsView";
import { FileExplorer } from "@/components/file-explorer";
import { Skeleton } from "@/components/ui/skeleton";
import { db } from "@/db";
import { AgentEnvironmentProvider } from "@/contexts/AgentEnvironmentContext";
import type { AgentConfig } from "@/agent/AgentEnvironment";
import { useAuth } from "@/hooks/use-auth";
import { useUser } from "@/hooks/use-user";
import { LoginView } from "@/components/auth/LoginView";
import { UpsellModal } from "@/components/auth/UpsellModal";

const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 2,
      refetchOnWindowFocus: true,
    },
  },
});

class ChatErrorBoundary extends Component<{ children: ReactNode }, { error: Error | null }> {
  state: { error: Error | null } = { error: null };

  static getDerivedStateFromError(error: Error) {
    return { error };
  }

  render() {
    if (this.state.error) {
      return (
        <div className="flex flex-col h-full p-3">
          <div className="text-destructive">
            <p className="font-medium">Failed to load chat</p>
            <p className="text-sm text-muted-foreground mt-1">{this.state.error.message}</p>
          </div>
        </div>
      );
    }
    return this.props.children;
  }
}

function ChatLoadingSkeleton() {
  return (
    <div className="flex flex-col h-full p-3 space-y-3">
      <div className="flex-1 space-y-3">
        {[1, 2, 3].map((i) => (
          <Skeleton key={i} className="h-16 w-full rounded-lg" />
        ))}
      </div>
      <Skeleton className="h-10 w-full rounded-md" />
    </div>
  );
}

function SettingsLoadingSkeleton() {
  return (
    <>
      <div className="flex items-center justify-between px-3 py-2 border-b border-border">
        <Skeleton className="h-5 w-20" />
        <Skeleton className="size-6 rounded-md" />
      </div>
      <div className="p-3 border-b border-border">
        <Skeleton className="h-8 w-full rounded-md" />
      </div>
      <div className="p-3 space-y-2">
        {[1, 2, 3].map((i) => (
          <Skeleton key={i} className="h-16 w-full rounded-lg" />
        ))}
      </div>
    </>
  );
}

// Inner component that requires QueryClientProvider
function AppContent({ logout }: { logout: () => void }) {
  const user = useUser();
  const {
    view,
    activeThreadId,
    loadSettings,
    isSettingsLoaded,
    geminiApiKey,
    useProxy,
    provider,
    anthropicModel,
    anthropicOAuthCredentials,
    openaiModel,
    openaiOAuthCredentials,
    showSubscriptionRequired,
    setShowSubscriptionRequired,
    hasPendingOmniboxMessage,
    navigateToNewChat,
  } = useAppStore();

  // Handle dismissing the upsell modal (retry after subscribing)
  const handleDismissUpsell = useCallback(() => {
    setShowSubscriptionRequired(false);
  }, [setShowSubscriptionRequired]);

  // Handle opening files from file explorer
  const handleOpenFile = useCallback((path: string) => {
    const viewerUrl = `chrome-extension://${chrome.runtime.id}/viewer.html?file=${encodeURIComponent(path)}`;
    chrome.tabs.create({ url: viewerUrl });
  }, []);

  // Load settings from chrome.storage on mount
  useEffect(() => {
    loadSettings();
  }, [loadSettings]);

  // Navigate to new chat when omnibox message is pending
  // ChatView will consume and process the actual message
  useEffect(() => {
    if (!isSettingsLoaded) return;

    async function checkAndNavigate(): Promise<void> {
      if (await hasPendingOmniboxMessage()) {
        navigateToNewChat();
      }
    }

    // Check on mount
    checkAndNavigate();

    // Listen for new messages while sidepanel is open
    function handleStorageChange(
      changes: { [key: string]: chrome.storage.StorageChange },
      areaName: string
    ): void {
      if (areaName === "local" && changes.pendingOmniboxMessage?.newValue) {
        navigateToNewChat();
      }
    }

    chrome.storage.onChanged.addListener(handleStorageChange);
    return () => {
      chrome.storage.onChanged.removeListener(handleStorageChange);
    };
  }, [isSettingsLoaded, hasPendingOmniboxMessage, navigateToNewChat]);

  // Build agent config from store settings (only when we have a threadId)
  const agentConfig = useMemo(
    (): AgentConfig | null =>
      activeThreadId
        ? {
            geminiApiKey,
            useProxy,
            provider,
            anthropicModel,
            anthropicOAuthCredentials,
            openaiModel,
            openaiOAuthCredentials,
            threadId: activeThreadId,
          }
        : null,
    [geminiApiKey, useProxy, provider, anthropicModel, anthropicOAuthCredentials, openaiModel, openaiOAuthCredentials, activeThreadId]
  );

  // Get current thread title for header
  const currentThread = useLiveQuery(
    () => (activeThreadId ? db.threads.get(activeThreadId) : undefined),
    [activeThreadId]
  );

  const headerTitle =
    view === "settings"
      ? "Settings"
      : view === "chat"
        ? currentThread?.title || "Chat"
        : view === "files"
          ? "Files"
          : undefined;

  // Model ID for pricing calculation
  const modelId = provider === "anthropic-oauth" ? anthropicModel : provider === "openai-oauth" ? openaiModel : "gemini-3-flash-preview";

  return (
    <div className="flex flex-col h-screen bg-background">
      {!isSettingsLoaded ? (
        <SettingsLoadingSkeleton />
      ) : (
        <>
          <AnnouncementBanner />
          <Header title={headerTitle} tokenUsage={currentThread?.tokenUsage} modelId={modelId} />
          <main className="flex-1 overflow-hidden">
            {view === "threads" && <ThreadListView />}
            {view === "chat" && agentConfig && (
              <AgentEnvironmentProvider config={agentConfig}>
                <ChatErrorBoundary>
                  <Suspense fallback={<ChatLoadingSkeleton />}>
                    <ChatView />
                  </Suspense>
                </ChatErrorBoundary>
              </AgentEnvironmentProvider>
            )}
            {view === "settings" && <SettingsView logout={logout} />}
            {view === "files" && <FileExplorer onOpenFile={handleOpenFile} />}
          </main>

          {/* Subscription upsell modal - shown when chat request returns 402 */}
          <UpsellModal
            open={showSubscriptionRequired}
            userEmail={user?.email}
            onRefresh={handleDismissUpsell}
            onClose={handleDismissUpsell}
          />
        </>
      )}
    </div>
  );
}

function App() {
  const { authState, login, logout } = useAuth();

  // Handle sidepanel toggle via keyboard shortcut
  useEffect(() => {
    // Send heartbeat to background so it knows we're open
    const sendHeartbeat = () => {
      chrome.runtime.sendMessage({ type: "sidepanel-heartbeat" });
    };
    sendHeartbeat(); // Send immediately
    const heartbeatInterval = setInterval(sendHeartbeat, 500);

    // Listen for close command from background
    const handleMessage = (message: { type: string }) => {
      if (message.type === "close-sidepanel") {
        window.close();
      }
    };
    chrome.runtime.onMessage.addListener(handleMessage);

    return () => {
      clearInterval(heartbeatInterval);
      chrome.runtime.onMessage.removeListener(handleMessage);
    };
  }, []);

  // Show login view when logged out or error
  if (authState.status === "loggedOut" || authState.status === "error") {
    return (
      <div className="h-screen bg-background">
        <LoginView authState={authState} onLogin={login} />
      </div>
    );
  }

  // Show loading skeleton while checking auth
  if (authState.status === "loading") {
    return (
      <div className="flex flex-col h-screen bg-background">
        <div className="flex items-center justify-between px-3 py-2 border-b border-border">
          <Skeleton className="h-5 w-20" />
          <Skeleton className="size-6 rounded-md" />
        </div>
        <div className="flex-1 flex items-center justify-center">
          <div className="text-sm text-muted-foreground">Loading...</div>
        </div>
      </div>
    );
  }

  return (
    <QueryClientProvider client={queryClient}>
      <AppContent logout={logout} />
    </QueryClientProvider>
  );
}

export default App;

