  </ErrorBoundary>
);

import { useCallback } from "react";
import { logger } from "@dobrowser/logger";
import { ArrowLeft, Plus, Settings } from "lucide-react";

const log = logger.scoped("summary");
import { useAppStore } from "@/stores/app-store";
import { createThread, generateThreadId, getThread, type TokenUsageStats } from "@/db";
import { Button } from "@/components/ui/button";
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip";
import { ThreadMenu } from "@/components/chat/ThreadMenu";
import { cn } from "@/lib/utils";

interface HeaderProps {
  title?: string;
  className?: string;
  tokenUsage?: TokenUsageStats | null;
  modelId?: string;
}

export function Header({ title, className, tokenUsage, modelId }: HeaderProps) {
  const {
    view,
    activeThreadId,
    navigateToThreads,
    navigateToSettings,
    navigateToChat,
    isCompacting,
    compactHandler,
    hasSummary,
  } = useAppStore();

  const handleCopyJson = useCallback(async () => {
    if (!activeThreadId) return;

    const thread = await getThread(activeThreadId);
    if (!thread?.messages) return;

    // Strip image data and provider metadata from messages
    const messagesJson = JSON.stringify(
      thread.messages,
      (key, value) => {
        // Strip callProviderMetadata entirely
        if (key === "callProviderMetadata") {
          return undefined;
        }
        // Strip images array from tool output
        if (key === "images" && Array.isArray(value)) {
          return value.length > 0 ? `[${value.length} image(s) stripped]` : value;
        }
        // Replace base64 image data with placeholder
        if (typeof value === "string" && value.startsWith("data:image/")) {
          return "[IMAGE_DATA]";
        }
        return value;
      },
      2
    );

    await navigator.clipboard.writeText(messagesJson);
  }, [activeThreadId]);

  const handleCompact = useCallback(() => {
    compactHandler?.();
  }, [compactHandler]);

  const handleViewSummary = useCallback(async () => {
    if (!activeThreadId) return;

    const thread = await getThread(activeThreadId);
    if (!thread?.summary) {
      log.log("No summary available for this thread");
      return;
    }

    log.log("Thread summary:");
    log.log("─".repeat(60));
    log.log(thread.summary);
    log.log("─".repeat(60));
    log.log(`Summarized up to message index: ${thread.summarizedUpToIndex}`);
  }, [activeThreadId]);

  const handleNewChat = useCallback(async () => {
    const id = generateThreadId();
    await createThread(id);
    navigateToChat(id);
  }, [navigateToChat]);

  const showBackButton = view === "chat" || view === "settings" || view === "files";

  return (
    <header
      className={cn(
        "flex items-center justify-between px-3 py-2",
        "border-b border-border bg-background",
        "sticky top-0 z-10",
        className
      )}
    >
      <div className="flex items-center gap-2 min-w-0 flex-1 mr-2">
        {showBackButton && (
          <Tooltip>
            <TooltipTrigger asChild>
              <Button
                variant="ghost"
                size="icon"
                onClick={navigateToThreads}
                aria-label="Back to threads"
                className="shrink-0"
              >
                <ArrowLeft />
              </Button>
            </TooltipTrigger>
            <TooltipContent side="bottom">Back to threads</TooltipContent>
          </Tooltip>
        )}
        <h1 className="text-base font-semibold truncate">
          {title || (view === "settings" ? "Settings" : "Do Browser")}
        </h1>
      </div>

      <div className="flex items-center shrink-0">
        {view === "chat" && import.meta.env.DEV && (
          <ThreadMenu
            onCompact={handleCompact}
            onCopyJson={handleCopyJson}
            onViewSummary={handleViewSummary}
            isCompacting={isCompacting}
            hasSummary={hasSummary}
            tokenUsage={tokenUsage}
            modelId={modelId}
          />
        )}

        {view === "threads" && (
          <Tooltip>
            <TooltipTrigger asChild>
              <Button
                variant="ghost"
                size="icon"
                onClick={navigateToSettings}
                aria-label="Settings"
              >
                <Settings className="size-4" />
              </Button>
            </TooltipTrigger>
            <TooltipContent side="bottom">Settings</TooltipContent>
          </Tooltip>
        )}

        {view === "chat" && (
          <Tooltip>
            <TooltipTrigger asChild>
              <Button variant="ghost" size="icon" onClick={handleNewChat} aria-label="New chat">
                <Plus className="size-4" />
              </Button>
            </TooltipTrigger>
            <TooltipContent side="bottom">New chat</TooltipContent>
          </Tooltip>
        )}
      </div>
    </header>
  );
}

import { useRef, useEffect, useState } from "react";
import { Brain, ChevronRight, FileText, Loader2 } from "lucide-react";
import { ToolCallCard } from "./ToolCallCard";
import { MarkdownContent } from "./MarkdownContent";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Collapsible, CollapsibleTrigger, CollapsibleContent } from "@/components/ui/collapsible";
import { cn } from "@/lib/utils";
import type { UIMessage } from "@/lib/message-types";
import { formatBytes } from "@/lib/attachment-types";
import { parseFileAttachmentTags } from "@/lib/chat-attachment-tag";

interface MessageListProps {
  messages: UIMessage[];
  isLoading: boolean;
  /** Index of the last message that was summarized (divider appears after this message) */
  summarizedUpToIndex?: number | null;
  /** Callback when user clicks a suggestion in the empty state */
  onSuggestionClick?: (suggestion: string) => void;
}

// Extract the last bold thought from reasoning text (text between **)
const BOLD_THOUGHT_RE = /\*\*([^*]+)\*\*/g;
const BOLD_STRIP_RE = /\*\*/g;
function getLastBoldThought(text: string): string | null {
  const matches = text.match(BOLD_THOUGHT_RE);
  if (!matches || matches.length === 0) return null;
  const last = matches[matches.length - 1];
  return last.replace(BOLD_STRIP_RE, "").trim();
}

function ReasoningBlock({ text, state }: { text: string; state: "streaming" | "done" }) {
  const [isExpanded, setIsExpanded] = useState(false);
  const isStreaming = state === "streaming";

  const lastThought = getLastBoldThought(text);
  const title = isStreaming ? lastThought || "Thinking..." : lastThought || "Reasoned";

  return (
    <Collapsible open={isExpanded} onOpenChange={setIsExpanded}>
      <CollapsibleTrigger asChild>
        <button
          className={cn(
            "group flex items-start gap-1.5 text-left",
            "text-sm text-foreground/70 italic",
            "hover:text-foreground transition-colors"
          )}
        >
          {isStreaming ? (
            <Loader2 className="size-3.5 mt-1 text-foreground/50 animate-spin flex-shrink-0" />
          ) : (
            <Brain className="size-3.5 mt-1 text-foreground/50 flex-shrink-0" />
          )}
          <span>{title}</span>
          <ChevronRight
            className={cn(
              "size-3.5 mt-1 flex-shrink-0 transition-all",
              "opacity-0 group-hover:opacity-100",
              isExpanded && "rotate-90"
            )}
          />
        </button>
      </CollapsibleTrigger>

      <CollapsibleContent>
        <div className="mt-2 ml-4 text-sm">
          <pre className="p-2 rounded bg-muted/50 overflow-x-auto font-mono text-xs whitespace-pre-wrap">
            {text || "Processing..."}
          </pre>
        </div>
      </CollapsibleContent>
    </Collapsible>
  );
}

function MessageContent({ message }: { message: UIMessage }) {
  const isUser = message.role === "user";

  return (
    <div className="space-y-2">
      {message.parts.map((part, index) => {
        // Filter out stray periods that Gemini outputs after reasoning blocks
        if (part.type === "text" && part.text.trim() && part.text.trim() !== ".") {
          if (isUser) {
            const segments = parseFileAttachmentTags(part.text);
            return (
              <div key={index} className="text-sm leading-relaxed whitespace-pre-wrap">
                {segments.map((segment, segmentIndex) => {
                  if (segment.type === "text") {
                    return <span key={`text-${segmentIndex}`}>{segment.text}</span>;
                  }
                  return (
                    <span
                      key={`attachment-${segmentIndex}`}
                      className="inline-flex items-center gap-1.5 rounded-md border border-primary-foreground/25 bg-primary-foreground/15 px-2 py-1 align-middle my-0.5"
                    >
                      <FileText className="size-3.5 opacity-90" />
                      <span className="text-xs">{segment.attachment.filename}</span>
                      <span className="text-xs opacity-80">{formatBytes(segment.attachment.size)}</span>
                    </span>
                  );
                })}
              </div>
            );
          }
          return (
            <div key={index} className="text-sm leading-relaxed">
              <MarkdownContent content={part.text.trim()} />
            </div>
          );
        }
        if (part.type === "file" && part.mediaType?.startsWith("image/")) {
          return (
            <img
              key={index}
              src={part.url}
              alt={part.filename || "Attached image"}
              className="max-w-full rounded-lg border border-border"
            />
          );
        }
        if (part.type === "reasoning") {
          return <ReasoningBlock key={index} text={part.text} state={part.state || "done"} />;
        }
        if (part.type === "tool-call") {
          return <ToolCallCard key={part.toolCallId} toolPart={part} />;
        }
        return null;
      })}
    </div>
  );
}

function Message({ message }: { message: UIMessage }) {
  const isUser = message.role === "user";

  if (isUser) {
    return (
      <div className="flex justify-end">
        <div className="max-w-[85%] px-3 py-2 rounded-lg bg-primary text-primary-foreground rounded-tr-sm">
          <MessageContent message={message} />
        </div>
      </div>
    );
  }

  return (
    <div className="text-foreground">
      <MessageContent message={message} />
    </div>
  );
}

function LoadingIndicator() {
  return (
    <div className="flex justify-start" role="status" aria-live="polite" aria-label="Assistant is working">
      <div className="flex items-center gap-0.5">
        <div className="size-1.5 rounded-full bg-foreground/40 animate-bounce [animation-duration:900ms]" />
        <div className="size-1.5 rounded-full bg-foreground/40 animate-bounce [animation-delay:150ms] [animation-duration:900ms]" />
        <div className="size-1.5 rounded-full bg-foreground/40 animate-bounce [animation-delay:300ms] [animation-duration:900ms]" />
      </div>
    </div>
  );
}

const suggestions = [
  "Create a slideshow",
  "Summarize a YouTube video",
  "Send an email",
];

function EmptyState({ onSuggestionClick }: { onSuggestionClick?: (suggestion: string) => void }) {
  return (
    <div className="flex flex-col items-center justify-center h-full p-6 text-center">
      <h2 className="text-base font-medium text-foreground mb-4">What can I do for you?</h2>
      {onSuggestionClick && (
        <div className="flex flex-wrap gap-2 justify-center max-w-[280px]">
          {suggestions.map((suggestion) => (
            <button
              key={suggestion}
              onClick={() => onSuggestionClick(suggestion)}
              className="px-3 py-1.5 text-sm bg-muted hover:bg-muted/80 text-foreground rounded-full border border-border transition-colors"
            >
              {suggestion}
            </button>
          ))}
        </div>
      )}
      <p className="text-xs text-muted-foreground mt-6">
        AI can make mistakes. Always supervise Do Browser.
      </p>
    </div>
  );
}

function SummaryDivider() {
  return (
    <div className="flex items-center gap-2 text-xs text-muted-foreground py-2">
      <div className="flex-1 border-t border-border" />
      <span>Earlier messages summarized</span>
      <div className="flex-1 border-t border-border" />
    </div>
  );
}

export function MessageList({ messages, isLoading, summarizedUpToIndex, onSuggestionClick }: MessageListProps) {
  const bottomRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isLoading]);

  if (messages.length === 0 && !isLoading) {
    return <EmptyState onSuggestionClick={onSuggestionClick} />;
  }

  const showDividerAtTop = summarizedUpToIndex != null && summarizedUpToIndex >= messages.length;
  const showDividerAtIndex = summarizedUpToIndex != null && summarizedUpToIndex < messages.length;

  return (
    <ScrollArea className="flex-1 min-h-0">
      <div className="p-3 space-y-2">
        {showDividerAtTop && <SummaryDivider />}
        {messages.map((message, index) => {
          return (
            <div key={message.id}>
              <Message message={message} />
              {showDividerAtIndex && index === summarizedUpToIndex && <SummaryDivider />}
            </div>
          );
        })}
        {isLoading && <LoadingIndicator />}
        <div ref={bottomRef} />
      </div>
    </ScrollArea>
  );
}

import {
  useState,
  useRef,
  useEffect,
  useCallback,
  useImperativeHandle,
  forwardRef,
  type FormEvent,
  type KeyboardEvent,
  type ChangeEvent,
  type ClipboardEvent,
} from "react";
import { ArrowUp, Square, Clock, X, Paperclip } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip";
import { cn } from "@/lib/utils";
import { AttachedAttachments } from "./AttachedAttachments";
import { ThinkingLevelToggle } from "./ThinkingLevelToggle";
import {
  type ChatAttachment,
  MAX_TEXT_ATTACHMENT_SIZE_BYTES,
  fileToImageAttachment,
  fileToTextAttachment,
} from "@/lib/attachment-types";
import { FREE_MESSAGE_LIMIT } from "@dobrowser/redo-api-contract";
import { useAppStore } from "@/stores/app-store";
import { useSubscriptionStatus } from "@/hooks/use-subscription-status";
import { createNotifier, NEW_CHAT_SOURCE_ID } from "@/stores/notification-store";

const API_BASE_URL = import.meta.env.VITE_API_BASE_URL || "https://www.dobrowser.io";

interface MessageInputProps {
  onSend: (message: string, attachments?: ChatAttachment[]) => void;
  onStop?: () => void;
  isLoading: boolean;
  disabled?: boolean;
  queuedMessage?: string;
  onQueue?: (message: string) => void;
  onClearQueue?: () => string;
}

export interface MessageInputHandle {
  setInput: (value: string) => void;
  focus: () => void;
  addFiles: (files: File[]) => Promise<void>;
}

export const MessageInput = forwardRef<MessageInputHandle, MessageInputProps>(function MessageInput({
  onSend,
  onStop,
  isLoading,
  disabled = false,
  queuedMessage = "",
  onQueue,
  onClearQueue,
}, ref) {
  const [input, setInput] = useState("");
  const [attachedAttachments, setAttachedAttachments] = useState<ChatAttachment[]>([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const messageHistory = useAppStore((state) => state.messageHistory);
  const addToMessageHistory = useAppStore((state) => state.addToMessageHistory);
  const useProxy = useAppStore((state) => state.useProxy);
  const activeThreadId = useAppStore((state) => state.activeThreadId);
  const { data: subscriptionData } = useSubscriptionStatus(useProxy);
  const [bannerDismissed, setBannerDismissed] = useState(false);
  const notifier = createNotifier(activeThreadId ?? NEW_CHAT_SOURCE_ID);

  useEffect(() => {
    if (!disabled && !isLoading) {
      textareaRef.current?.focus();
    }
  }, [disabled, isLoading]);

  const addToHistory = useCallback(
    (message: string) => {
      if (!message.trim()) return;
      addToMessageHistory(message);
      setHistoryIndex(-1);
    },
    [addToMessageHistory]
  );

  const handlePreviousEntry = useCallback(() => {
    if (messageHistory.length === 0) return;

    const newIndex =
      historyIndex === -1 ? messageHistory.length - 1 : Math.max(0, historyIndex - 1);

    setHistoryIndex(newIndex);
    const message = messageHistory[newIndex];
    if (message !== undefined) {
      setInput(message);
    }
  }, [messageHistory, historyIndex]);

  const handleNextEntry = useCallback(() => {
    if (historyIndex === -1) return;

    const newIndex = historyIndex + 1;
    if (newIndex >= messageHistory.length) {
      setHistoryIndex(-1);
      setInput("");
    } else {
      setHistoryIndex(newIndex);
      const message = messageHistory[newIndex];
      if (message !== undefined) {
        setInput(message);
      }
    }
  }, [messageHistory, historyIndex]);

  const buildAttachmentsFromFiles = useCallback(
    async (files: File[]): Promise<ChatAttachment[]> => {
      const attachments: ChatAttachment[] = [];
      const imageFiles: File[] = [];

      for (const file of files) {
        const textAttachment = fileToTextAttachment(file);
        if (textAttachment) {
          if (file.size > MAX_TEXT_ATTACHMENT_SIZE_BYTES) {
            notifier.error("Attachment too large", [
              `${file.name} exceeds 2MB and cannot be attached.`,
            ]);
            continue;
          }
          attachments.push(textAttachment);
          continue;
        }

        imageFiles.push(file);
      }

      if (imageFiles.length > 0) {
        const imageAttachments = await Promise.all(imageFiles.map(fileToImageAttachment));
        attachments.push(...(imageAttachments.filter(Boolean) as ChatAttachment[]));
      }

      const acceptedFileCount = attachments.length;
      if (acceptedFileCount < files.length) {
        notifier.error("Unsupported attachment type", [
          "Only images and plain text files are supported.",
        ]);
      }

      return attachments;
    },
    [notifier]
  );

  const addFiles = useCallback(
    async (files: File[]) => {
      if (disabled || files.length === 0) return;
      const newAttachments = await buildAttachmentsFromFiles(files);
      setAttachedAttachments((prev) => [...prev, ...newAttachments]);
    },
    [buildAttachmentsFromFiles, disabled]
  );

  useImperativeHandle(ref, () => ({
    setInput: (value: string) => {
      setInput(value);
      setHistoryIndex(-1);
      textareaRef.current?.focus();
    },
    focus: () => {
      textareaRef.current?.focus();
    },
    addFiles,
  }));

  const handleFileSelect = async (e: ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    await addFiles(files);
    // Reset input to allow re-selecting the same file
    e.target.value = "";
  };

  const handlePaste = async (e: ClipboardEvent<HTMLTextAreaElement>) => {
    const imageFiles = Array.from(e.clipboardData.items)
      .filter((item) => item.type.startsWith("image/"))
      .map((item) => item.getAsFile())
      .filter((file): file is File => file !== null);

    if (imageFiles.length === 0) return;

    e.preventDefault();
    const newAttachments = await Promise.all(imageFiles.map(fileToImageAttachment));
    setAttachedAttachments((prev) => [
      ...prev,
      ...(newAttachments.filter(Boolean) as ChatAttachment[]),
    ]);
  };

  const handleRemoveAttachment = (id: string) => {
    setAttachedAttachments((prev) => prev.filter((attachment) => attachment.id !== id));
  };

  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();
    const hasContent = input.trim() || attachedAttachments.length > 0;
    if (!hasContent || disabled) return;

    if (isLoading && onQueue) {
      if (attachedAttachments.length > 0) {
        notifier.error("Attachments cannot be queued", [
          "Wait for the current response to finish before sending attachments.",
        ]);
        return;
      }

      // Queue the message if agent is working
      if (input.trim()) {
        onQueue(input.trim());
        addToHistory(input.trim());
        setInput("");
      }
    } else if (!isLoading) {
      // Send immediately if agent is idle
      onSend(input.trim(), attachedAttachments.length > 0 ? attachedAttachments : undefined);
      addToHistory(input.trim());
      setInput("");
      setAttachedAttachments([]);
    }
  };

  const handleClearQueue = () => {
    const text = onClearQueue?.();
    if (text) {
      setInput(text);
      setHistoryIndex(-1);
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    const { selectionStart, selectionEnd, value } = e.currentTarget;

    if (e.key === "ArrowUp" && selectionStart === 0 && selectionEnd === 0) {
      e.preventDefault();
      handlePreviousEntry();
      return;
    }

    if (e.key === "ArrowDown" && selectionStart === value.length && selectionEnd === value.length) {
      e.preventDefault();
      handleNextEntry();
      return;
    }

    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  // Can submit if has content (text or attachments), not disabled, and either idle or can queue
  const hasContent = input.trim() || attachedAttachments.length > 0;
  const canSubmit = hasContent && !disabled && (!isLoading || Boolean(onQueue));

  const submitButton = isLoading ? (
    <Tooltip>
      <TooltipTrigger asChild>
        <Button
          type="button"
          onClick={onStop}
          size="icon"
          className="size-7 rounded-full bg-foreground text-background hover:bg-foreground/90"
        >
          <Square className="size-2.5 fill-current" />
        </Button>
      </TooltipTrigger>
      <TooltipContent>Stop generating</TooltipContent>
    </Tooltip>
  ) : (
    <Tooltip>
      <TooltipTrigger asChild>
        <Button
          type="submit"
          disabled={!canSubmit}
          size="icon"
          className={cn(
            "size-7 rounded-full transition-all",
            canSubmit
              ? "bg-primary text-primary-foreground hover:bg-primary/90 active:scale-95"
              : "bg-muted text-muted-foreground/40 cursor-default"
          )}
        >
          <ArrowUp className="size-3.5" strokeWidth={2.5} />
        </Button>
      </TooltipTrigger>
      <TooltipContent>Send message</TooltipContent>
    </Tooltip>
  );

  return (
    <form onSubmit={handleSubmit} className="sticky bottom-0 p-3 bg-background">
      <div className="border border-border rounded-2xl overflow-hidden">
        {/* Queued message chin */}
        {queuedMessage && (
          <div className="flex items-center gap-2 px-4 py-2 bg-muted/50 border-b border-border">
            <Clock className="size-4 text-muted-foreground shrink-0" />
            <span className="flex-1 text-sm text-foreground truncate">{queuedMessage}</span>
            <Button
              type="button"
              variant="ghost"
              size="icon"
              className="size-6 shrink-0"
              onClick={handleClearQueue}
            >
              <X className="size-3" />
            </Button>
          </div>
        )}

        {/* Free messages banner */}
        {!bannerDismissed && useProxy && subscriptionData && !subscriptionData.hasSubscription && (() => {
          const remaining = Math.max((subscriptionData.freeMessagesLimit ?? FREE_MESSAGE_LIMIT) - (subscriptionData.freeMessagesUsed ?? 0), 0);
          return (
            <div className="flex items-center gap-2 px-4 py-2 bg-muted/50 border-b border-border">
              <span className="flex-1 text-sm text-foreground truncate">
                {remaining} message{remaining !== 1 ? "s" : ""} left &middot;{" "}
                <button
                  type="button"
                  className="underline underline-offset-2 text-muted-foreground hover:text-foreground transition-colors"
                  onClick={() => window.open(`${API_BASE_URL}/settings`, "_blank")}
                >
                  Upgrade for more
                </button>
              </span>
              <Button
                type="button"
                variant="ghost"
                size="icon"
                className="size-6 shrink-0"
                onClick={() => setBannerDismissed(true)}
              >
                <X className="size-3" />
              </Button>
            </div>
          );
        })()}

        {/* Attached files/images preview */}
        <AttachedAttachments attachments={attachedAttachments} onRemove={handleRemoveAttachment} />

        <div className="group relative flex">
          <Textarea
            ref={textareaRef}
            value={input}
            onChange={(e) => {
              setInput(e.target.value);
              setHistoryIndex(-1);
            }}
            onKeyDown={handleKeyDown}
            onPaste={handlePaste}
            placeholder={disabled ? "Configure API key in Settings..." : "Type a message..."}
            disabled={disabled}
            rows={1}
            className={cn(
              "flex-1 border-0 bg-transparent shadow-none focus-visible:ring-0",
              "min-h-[68px] max-h-[50vh]",
              "px-4 pt-3 pb-11",
              // Hide scrollbar by default, show on hover
              "[&::-webkit-scrollbar]:w-1.5",
              "[&::-webkit-scrollbar-track]:bg-transparent",
              "[&::-webkit-scrollbar-thumb]:bg-transparent",
              "[&::-webkit-scrollbar-thumb]:rounded-full",
              "group-hover:[&::-webkit-scrollbar-thumb]:bg-border"
            )}
          />

          {/* Hidden file input */}
          <input
            ref={fileInputRef}
            type="file"
            accept="image/*,text/*,application/json,.md,.txt,.json,.yaml,.yml,.csv,.ts,.tsx,.js,.jsx,.log"
            multiple
            className="hidden"
            onChange={handleFileSelect}
            aria-label="Attach files"
          />

          {/* Bottom toolbar */}
          <div className="absolute bottom-1.5 left-1.5 right-1.5 z-10 flex items-center justify-between">
            <ThinkingLevelToggle />

            <div className="flex items-center gap-0.5">
              {/* Attachment button */}
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    type="button"
                    variant="ghost"
                    size="icon"
                    className="size-7 rounded-full text-muted-foreground/60 hover:text-foreground"
                    onClick={() => fileInputRef.current?.click()}
                    disabled={disabled}
                  >
                    <Paperclip className="size-3.5" />
                  </Button>
                </TooltipTrigger>
                <TooltipContent>Attach files</TooltipContent>
              </Tooltip>

              {submitButton}
            </div>
          </div>
        </div>
      </div>
    </form>
  );
});

import { useCallback, useEffect, useRef, useState, type DragEvent } from "react";
import { Loader2, X } from "lucide-react";
import { useRedoChat } from "@/hooks/use-redo-chat";
import { useAppStore, canChat } from "@/stores/app-store";
import { useAgentEnvironment } from "@/contexts/AgentEnvironmentContext";
import { MessageList } from "./MessageList";
import { MessageInput, type MessageInputHandle } from "./MessageInput";
import { NotificationIndicator } from "./NotificationIndicator";
import { Button } from "@/components/ui/button";

export function ChatView() {
  const activeThreadId = useAppStore((state) => state.activeThreadId);
  const geminiApiKey = useAppStore((state) => state.geminiApiKey);
  const useProxy = useAppStore((state) => state.useProxy);
  const provider = useAppStore((state) => state.provider);
  const anthropicOAuthCredentials = useAppStore((state) => state.anthropicOAuthCredentials);
  const setCompactionState = useAppStore((state) => state.setCompactionState);
  const consumePendingOmniboxMessage = useAppStore((state) => state.consumePendingOmniboxMessage);
  const addToMessageHistory = useAppStore((state) => state.addToMessageHistory);

  const { environment } = useAgentEnvironment();
  const messageInputRef = useRef<MessageInputHandle>(null);
  const [isDraggingFiles, setIsDraggingFiles] = useState(false);
  const dragDepthRef = useRef(0);

  const {
    messages,
    isLoading,
    sendMessage,
    stopGeneration,
    queuedMessage,
    queueMessage,
    clearQueue,
    isCompacting,
    summaryState,
    triggerManualCompaction,
  } = useRedoChat({
    threadId: activeThreadId,
  });

  const openaiOAuthCredentials = useAppStore((state) => state.openaiOAuthCredentials);
  const isChatEnabled = canChat({ provider, useProxy, geminiApiKey, anthropicOAuthCredentials, openaiOAuthCredentials });

  // Register compaction state with app store so Header can access it
  const hasSummary = summaryState.summary != null;
  useEffect(() => {
    setCompactionState(isCompacting, triggerManualCompaction, hasSummary);
    return () => setCompactionState(false, null, false);
  }, [isCompacting, triggerManualCompaction, hasSummary, setCompactionState]);

  // Process pending omnibox message once per active chat thread.
  const lastProcessedThreadId = useRef<string | null>(null);

  useEffect(() => {
    if (!activeThreadId || !isChatEnabled) return;
    if (lastProcessedThreadId.current === activeThreadId) return;
    lastProcessedThreadId.current = activeThreadId;

    consumePendingOmniboxMessage().then((message) => {
      if (message) {
        void addToMessageHistory(message);
        void sendMessage(message);
      }
    });
  }, [activeThreadId, isChatEnabled, consumePendingOmniboxMessage, addToMessageHistory, sendMessage]);

  const handleCancelCompaction = useCallback(() => {
    environment?.cancelCompaction();
  }, [environment]);

  const handleSuggestionClick = useCallback((suggestion: string) => {
    messageInputRef.current?.setInput(suggestion);
  }, []);

  const handleDragEnter = useCallback((e: DragEvent<HTMLDivElement>) => {
    if (!e.dataTransfer?.types.includes("Files")) return;
    e.preventDefault();
    dragDepthRef.current += 1;
    setIsDraggingFiles(true);
  }, []);

  const handleDragOver = useCallback((e: DragEvent<HTMLDivElement>) => {
    if (!e.dataTransfer?.types.includes("Files")) return;
    e.preventDefault();
    e.dataTransfer.dropEffect = "copy";
  }, []);

  const handleDragLeave = useCallback((e: DragEvent<HTMLDivElement>) => {
    if (!e.dataTransfer?.types.includes("Files")) return;
    e.preventDefault();
    dragDepthRef.current = Math.max(0, dragDepthRef.current - 1);
    if (dragDepthRef.current === 0) {
      setIsDraggingFiles(false);
    }
  }, []);

  const handleDrop = useCallback((e: DragEvent<HTMLDivElement>) => {
    if (!e.dataTransfer?.types.includes("Files")) return;
    e.preventDefault();
    dragDepthRef.current = 0;
    setIsDraggingFiles(false);
    const files = Array.from(e.dataTransfer.files || []);
    if (files.length === 0) return;
    void messageInputRef.current?.addFiles(files);
  }, []);

  return (
    <div
      className="relative flex flex-col h-full"
      onDragEnter={handleDragEnter}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
    >
      {isDraggingFiles && (
        <div className="pointer-events-none absolute inset-2 z-50 rounded-lg border-2 border-dashed border-primary bg-primary/10" />
      )}
      {/* Compacting indicator */}
      {isCompacting && (
        <div className="flex items-center gap-2 text-sm text-muted-foreground px-3 py-2 bg-muted/50 border-b border-border">
          <Loader2 className="size-3.5 animate-spin" />
          <span className="flex-1">Compacting conversation...</span>
          <Button variant="ghost" size="icon-xs" onClick={handleCancelCompaction}>
            <X className="size-3" />
          </Button>
        </div>
      )}

      <MessageList
        messages={messages}
        isLoading={isLoading}
        summarizedUpToIndex={summaryState.summarizedUpToIndex}
        onSuggestionClick={handleSuggestionClick}
      />

      {/* Input area with notification indicator */}
      <div className="relative">
        <NotificationIndicator sourceId={activeThreadId ?? undefined} />
        <MessageInput
          ref={messageInputRef}
          onSend={sendMessage}
          onStop={stopGeneration}
          isLoading={isLoading}
          disabled={!isChatEnabled}
          queuedMessage={queuedMessage}
          onQueue={queueMessage}
          onClearQueue={clearQueue}
        />
      </div>
    </div>
  );
}

