{"path":"apps/ui/src/components/chat/MessageInput.tsx","content":"import {
  forwardRef,
  useCallback,
  useEffect,
  useImperativeHandle,
  useRef,
  useState,
  type FormEvent,
  type KeyboardEvent,
} from 'react'
import { ArrowUp, Loader2 } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { cn } from '@/lib/utils'

interface MessageInputProps {
  onSend: (message: string) => void
  isLoading: boolean
  disabled?: boolean
}

export interface MessageInputHandle {
  setInput: (value: string) => void
  focus: () => void
}

export const MessageInput = forwardRef<MessageInputHandle, MessageInputProps>(function MessageInput(
  { onSend, isLoading, disabled = false },
  ref,
) {
  const [input, setInput] = useState('')
  const textareaRef = useRef<HTMLTextAreaElement | null>(null)

  const resizeTextarea = useCallback(() => {
    const textarea = textareaRef.current
    if (!textarea) return

    textarea.style.height = 'auto'
    textarea.style.height = `${Math.min(textarea.scrollHeight, 220)}px`
  }, [])

  useEffect(() => {
    resizeTextarea()
  }, [input, resizeTextarea])

  useEffect(() => {
    if (!disabled && !isLoading) {
      textareaRef.current?.focus()
    }
  }, [disabled, isLoading])

  useImperativeHandle(
    ref,
    () => ({
      setInput: (value: string) => {
        setInput(value)
        requestAnimationFrame(() => textareaRef.current?.focus())
      },
      focus: () => {
        textareaRef.current?.focus()
      },
    }),
    [],
  )

  const handleSubmit = useCallback(
    (event: FormEvent<HTMLFormElement>) => {
      event.preventDefault()
      const trimmed = input.trim()
      if (!trimmed || disabled || isLoading) return

      onSend(trimmed)
      setInput('')
    },
    [disabled, input, isLoading, onSend],
  )

  const handleKeyDown = (event: KeyboardEvent<HTMLTextAreaElement>) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault()
      const trimmed = input.trim()
      if (!trimmed || disabled || isLoading) return
      onSend(trimmed)
      setInput('')
    }
  }

  const canSubmit = input.trim().length > 0 && !disabled && !isLoading

  return (
    <form onSubmit={handleSubmit} className="sticky bottom-0 bg-background p-3">
      <div className="overflow-hidden rounded-2xl border border-border">
        <div className="group relative flex">
          <textarea
            ref={textareaRef}
            value={input}
            onChange={(event) => setInput(event.target.value)}
            onKeyDown={handleKeyDown}
            placeholder={disabled ? 'Waiting for connection...' : 'Type a message...'}
            disabled={disabled}
            rows={1}
            className={cn(
              'flex-1 resize-none border-0 bg-transparent text-sm text-foreground shadow-none focus:outline-none',
              'min-h-[68px] max-h-[220px]',
              'px-4 pt-3 pb-11',
              '[&::-webkit-scrollbar]:w-1.5',
              '[&::-webkit-scrollbar-track]:bg-transparent',
              '[&::-webkit-scrollbar-thumb]:bg-transparent',
              '[&::-webkit-scrollbar-thumb]:rounded-full',
              'group-hover:[&::-webkit-scrollbar-thumb]:bg-border',
            )}
          />

          <div className="absolute bottom-1.5 right-1.5 z-10 flex items-center gap-1">
            <Button
              type="submit"
              disabled={!canSubmit}
              size="icon"
              className={cn(
                'size-7 rounded-full transition-all',
                canSubmit
                  ? 'bg-primary text-primary-foreground hover:bg-primary/90 active:scale-95'
                  : 'cursor-default bg-muted text-muted-foreground/40',
              )}
            >
              {isLoading ? <Loader2 className="size-3.5 animate-spin" /> : <ArrowUp className="size-3.5" strokeWidth={2.5} />}
            </Button>
          </div>
        </div>
      </div>
    </form>
  )
})
"}

